void encoder_update_user(uint8_t index, bool clockwise) {
    // 0为左上角旋钮
    if (index == 0) {
        switch (get_highest_layer(layer_state)) {
            // 如果是音乐模式和默认win就是调节音量
            case _WIN:
            case _Music:
                if (clockwise) {
                    tap_code(KC_AUDIO_VOL_DOWN);
                } else {
                    tap_code(KC_AUDIO_VOL_UP);
                }
                break;
                // 设置灯关模式则是设置灯关亮度
            case _SET:
                if (clockwise) {
                    // 左转
                    rgblight_decrease_val();
                } else {
                    // 右旋
                    rgblight_increase_val();
                }
                break;
        }
    }
    if (index == 1) {
        // 1.左下角旋钮
        switch (get_highest_layer(layer_state)) {
            // pr和win则是调节左右
            case _WIN:
            case _PR:
                if (clockwise) {
                    tap_code(KC_LEFT);
                } else {
                    tap_code(KC_RIGHT);
                }
                break;
                // 音乐模式则调节音量
            case _Music:
                if (clockwise) {
                    tap_code(KC_AUDIO_VOL_DOWN);
                } else {
                    tap_code(KC_AUDIO_VOL_UP);
                }
                break;
        }
    }
}
